(1279, 141, 'especificaciones', '<table class="prodspecs alignleft">\r\n<tbody>\r\n<tr>\r\n<th>Tipo de proyector</th>\r\n<td>Proyector digital DLP de 3 chips y resolución WUXGA</td>\r\n</tr>\r\n<tr>\r\n<th>tecnología</th>\r\n<td>0,96" DMD™ x3</td>\r\n</tr>\r\n<tr>\r\n<th>resolución</th>\r\n<td>1.920 x 1.200</td>\r\n</tr>\r\n<tr>\r\n<th>brillo</th>\r\n<td>14.500 lúmenes centrales / 13.500 ANSI*</td>\r\n</tr>\r\n<tr>\r\n<th>relación de contraste</th>\r\n<td>1.650:1 (estándar)/2.400:1 (CE)</td>\r\n</tr>\r\n<tr>\r\n<th>brillo uniforme</th>\r\n<td>90%</td>\r\n</tr>\r\n<tr>\r\n<th>Relación de aspecto</th>\r\n<td>16:10</td>\r\n</tr>\r\n<tr>\r\n<th>ScenergiX</th>\r\n<td>Mezcla de borde horizontal y vertical</td>\r\n</tr>\r\n<tr>\r\n<th>Tipo de lente</th>\r\n<td>TLD+</td>\r\n</tr>\r\n<tr>\r\n<th>lentes</th>\r\n<td>0,73:1; 1,2:1; 1,16-1,5:1; 1,5-2,0:1; 2,0-2,8:1; 2,8-4,5:1; 4,5-7,5:1; 7,5-11,5:1</td>\r\n</tr>\r\n<tr>\r\n<th>Cambio de objetivo de la lente</th>\r\n<td>Vertical: −70% a +130%/Horizontal: +/−30% (en objetivos zoom con ajustes memorizados)</td>\r\n</tr>\r\n<tr>\r\n<th>Corrección del color</th>\r\n<td>P7</td>\r\n</tr>\r\n<tr>\r\n<th>Lámparas</th>\r\n<td>2 x 465 W</td>\r\n</tr>\r\n<tr>\r\n<th>Vida útil de la lámpara</th>\r\n<td>2.000 horas (típico)/2.500 horas (máximo)</td>\r\n</tr>\r\n<tr>\r\n<th>Garantía de la lámpara</th>\r\n<td>120 días, 500 horas</td>\r\n</tr>\r\n<tr>\r\n<th>núcleo DLP? estanco</th>\r\n<td>Estándar</td>\r\n</tr>\r\n<tr>\r\n<th>Obturador óptico</th>\r\n<td>Estándar</td>\r\n</tr>\r\n<tr>\r\n<th>Imagen dentro de la imagen</th>\r\n<td>Hasta dos fuentes de forma simultánea</td>\r\n</tr>\r\n<tr>\r\n<th>Orientación</th>\r\n<td>mesa - techo</td>\r\n</tr>\r\n<tr>\r\n<th>WARP</th>\r\n<td>valores predefinidos y personalizados (RS232)</td>\r\n</tr>\r\n<tr>\r\n<th>Servidor web integrado</th>\r\n<td>Sí</td>\r\n</tr>\r\n<tr>\r\n<th>3D</th>\r\n<td>DVI de enlace doble con entrada/salida BNC sincronizada</td>\r\n</tr>\r\n<tr>\r\n<th>entradas</th>\r\n<td>HDMI (HDCP 1,3); HDBase-T; 5-BNC (RGBHV, RGBS/RGBsB;YUV); VGA (RGBHV, RGBS/RGBsB;YUV); cinch (RGBs, YUV); HSDI/3G HDSDI/SDI; 3D DVI de enlace doble</td>\r\n</tr>\r\n<tr>\r\n<th>Resoluciones de entrada</th>\r\n<td>Desde NTSC a QXGA (1.600 x 1.200) incluyendo HDTV (1.920 x 1.200)</td>\r\n</tr>\r\n<tr>\r\n<th>Reloj de píxeles máx.</th>\r\n<td>165 Mhz hasta 200 Mhz</td>\r\n</tr>\r\n<tr>\r\n<th>Herramientas de software</th>\r\n<td>Projection Toolset + aplicación Android + aplicación iOS</td>\r\n</tr>\r\n<tr>\r\n<th>Control</th>\r\n<td>Conector mini-jack + IR, RS232, accionador de 12 v</td>\r\n</tr>\r\n<tr>\r\n<th>conexión de red</th>\r\n<td>10/100 base-T, conexión RJ-45</td>\r\n</tr>\r\n<tr>\r\n<th>Requisitos de alimentación</th>\r\n<td>90-240 V/50-60 Hz - 1.140 W a 240 V, 1.230 W a 110 V, STBY inferior a 2 W</td>\r\n</tr>\r\n<tr>\r\n<th>Nivel de ruido (típico a 25 °C/77 °F)</th>\r\n<td>46 dB(A) normal, 42 dB(A) económico</td>\r\n</tr>\r\n<tr>\r\n<th>Temperatura ambiente de funcionamiento</th>\r\n<td>0-40 °C/32°-104 °F</td>\r\n</tr>\r\n<tr>\r\n<th>Humedad operativa</th>\r\n<td>0-85 % (sin condensación)</td>\r\n</tr>\r\n<tr>\r\n<th>Disipación de BTU</th>\r\n<td>Máx. 4.131 BTU/h</td>\r\n</tr>\r\n<tr>\r\n<th>dimensiones (ancho x largo x alto)</th>\r\n<td>631 x 504 x 270 mm/24,8 x 19,82 x 10,63 pulgadas</td>\r\n</tr>\r\n<tr>\r\n<th>peso</th>\r\n<td>33,5 kg (73,8 lb)</td>\r\n</tr>\r\n<tr>\r\n<th>Dimensiones de embalaje</th>\r\n<td>(L x An. x Al.) 829 x 709 x 437 m/32,64 x 27,9 x 17,20 pulgadas</td>\r\n</tr>\r\n<tr>\r\n<th>Peso del envío desde fábrica</th>\r\n<td>41,5 kg (91,49 lb)</td>\r\n</tr>\r\n<tr>\r\n<th>Accesorios estándar</th>\r\n<td>Cable de corriente, control remoto inalámbrico, adaptador de HDMI a DVI</td>\r\n</tr>\r\n<tr>\r\n<th>certificaciones</th>\r\n<td>Cumple con las reglas y normativas de la FCC, parte 15 Clase A y E EN55022 Clase A, CE, TUV, RHoS, WEE</td>\r\n</tr>\r\n<tr>\r\n<th>Garantía</th>\r\n<td>Estándar de 3 años, opcional de 2 años más</td>\r\n</tr>\r\n<tr>\r\n<th>*</th>\r\n<td>+/- 10%</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
