(1656, 167, 'especificaciones', '<table cellspacing="0" cellpadding="2">\r\n<tbody>\r\n<tr>\r\n<td><span class="notranslate">Tecnología de visualización</span></td>\r\n<td><span class="notranslate">3LCD</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Brillo (ANSI)</span></td>\r\n<td><span class="notranslate">4.500 lúmenes</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Resolucion nativa</span></td>\r\n<td><span class="notranslate">XGA (1024 x 768)</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Max.</span> <span class="notranslate">Entrada de la computadora</span></td>\r\n<td><span class="notranslate">UXGA</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Max.</span> <span class="notranslate">Entrada de video</span></td>\r\n<td><span class="notranslate">1080i</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Relación de contraste</span></td>\r\n<td><span class="notranslate">1000: 1</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Tamaño del panel</span></td>\r\n<td><span class="notranslate">0,8 "(inorgánico)</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Tipo de lente (Tiro: Ancho)</span></td>\r\n<td><span class="notranslate">1.15-1.85: 1</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Cambio de lente de poder</span></td>\r\n<td><span class="notranslate">Hasta 40%</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Corrección trapezoidal</span></td>\r\n<td><span class="notranslate">U / D 40 °</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Entradas</span></td>\r\n<td><span class="notranslate">Dsub15 x 2;</span> <span class="notranslate">RCA x 1 o S-Video x 1;</span> <span class="notranslate">MiniStereo x 1;</span> <span class="notranslate">RCA (audio) x 2;</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Salidas</span></td>\r\n<td><span class="notranslate">Dsub15 x 1;</span> <span class="notranslate">MiniStereo x 1</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Sistema de sonido</span></td>\r\n<td><span class="notranslate">1 vatio;</span> <span class="notranslate">Altavoz de 1.1 "de diámetro x 1</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Control de red</span></td>\r\n<td><span class="notranslate">LAN cableada con entrega de contenido</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Garantía</span></td>\r\n<td><span class="notranslate">3 años / 6000 horas (lo que ocurra primero)</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Vida de la lámpara</span></td>\r\n<td><span class="notranslate">Hasta 3.000 horas</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Lámpara #</span></td>\r\n<td><span class="notranslate">610 333 9740</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Ruido del ventilador</span></td>\r\n<td><span class="notranslate">Tan bajo como 31 dBa</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Filtro de aire</span></td>\r\n<td><span class="notranslate">Estándar</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Paso máximo</span></td>\r\n<td><span class="notranslate">Hasta 360 °</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Peso</span></td>\r\n<td><span class="notranslate">7,7 lb (3,6 kg)</span></td>\r\n</tr>\r\n<tr>\r\n<td><span class="notranslate">Montaje en techo Número de parte:</span></td>\r\n<td><span class="notranslate">0173-4305</span></td>\r\n</tr>\r\n</tbody>\r\n</table>'),
